const TopCard = props => {
    let isShowing = false;
    let Window

    if (isShowing) {
        Window = <p>True</p>
    } else {
        Window = <p>False</p>
    }
    const changeState = () => {
        isShowing = !isShowing
    }
    return (
    <div>
        {Window}
        <button onClick={changeState}>Change the state</button>
    </div>
    )
}


export default TopCard;
