const TopCard = props => {
    let isShowing = false;
    let Window

    if (isShowing) {
        Window = <p>True</p>
    } else {
        Window = <p>False</p>
    }

    return (
    <div>
        Window
        <button onClick={(isShowing) => {isShowing = !isShowing}}></button>
    </div>
    )
}


export default TopCard;
