const TopCard = props => {
    let isShowing = false;
    let Window


    const changeState = () => {
        isShowing = !isShowing
        if (isShowing) {
            Window = <p>True</p>
        } else {
            Window = <p>False</p>
        }
    }



    return (
    <div>
        {Window}
        <button onClick={changeState}>Change the state</button>
    </div>
    )
}


export default TopCard;
